mapper.select:
select
id, user_name, title, url, img, `desc`, creation_time, is_public
from bookmark



            user_name = #{username}


    `is_public` = 1
    and `is_deleted` = 0



    HAVING creation_time  >=  #{fromDatetime,jdbcType=TIMESTAMP}
    AND creation_time  <=  #{toDatetime,jdbcType=TIMESTAMP}

order by ${orderField}

    desc



    , user_name asc



    , creation_time desc

limit 0, #{load}

select count(id)
from bookmark

    is_deleted = 0 and (`is_public` = 1

        or user_name = #{specUsername}

    )

;

select id, user_name, title, url, img, `desc`, creation_time, is_public from bookmark where user_name = #{userName}
and `is_public` = 1
and is_deleted = 0 order by id desc
limit #{from},#{size}

select count(user_name)
from bookmark
where user_name = #{userName}

    and `is_public` = 1

and is_deleted = 0
;

select
id, user_name, title, url, img, `desc`, creation_time, is_public
from bookmark
where user_name != #{excludeUsername}
and ( `is_public` = 1

    or user_name = #{includePrivateUsername}

) and is_deleted = 0
order by id desc limit #{from}, #{size};

select count(id)
from bookmark
where user_name != #{excludeUsername}
and ( `is_public` = 1

    or user_name = #{includePrivateUsername}

) and is_deleted = 0;

mapper.update:
update bookmark
set is_deleted = 1
where id = #{id,jdbcType=INTEGER};

update bookmark
set is_deleted = 1
where user_name = #{userName};

mapper.select:
select user_name
from bookmark
where id = #{id,jdbcType=INTEGER}
  and is_deleted = 0
limit 1;
