mapper.select:
select id, comment, bookmark_id, username, creation_time, reply_to_comment_id
from comment
where id = #{commentId,jdbcType=INTEGER}
  and is_deleted = 0
limit 1;

select username
from comment
where id = #{commentId,jdbcType=INTEGER}
  and is_deleted = 0
limit 1;

select `comment`
from comment
where id = #{commentId,jdbcType=INTEGER}
  and is_deleted = 0
limit 1;

select id, comment, bookmark_id, username, creation_time, reply_to_comment_id
from comment


        bookmark_id = #{bookmarkId,jdbcType=INTEGER}


        and reply_to_comment_id is null


        and reply_to_comment_id = #{replyToCommentId}


    and is_deleted = 0

order by `creation_time`

    desc


    limit 0, #{load}
;

select id, comment, bookmark_id, username, creation_time, reply_to_comment_id
from comment
where comment = #{comment}
  and username = #{username}
  and bookmark_id = #{bookmarkId}
  and is_deleted = 0
limit 1;

select count(*)
from comment
where reply_to_comment_id = #{countRepliesFromCommentId}
  and is_deleted = 0;

mapper.update:
update comment
set is_deleted = 1
where id = #{commentId,jdbcType=INTEGER}

update comment
set is_deleted = 1
where username = #{username};

update comment
set comment = #{comment,jdbcType=VARCHAR}
where id = #{commentId,jdbcType=INTEGER}

mapper.select:
select count(bookmark_id)
from comment
where reply_to_comment_id is null
  and bookmark_id = #{bookmarkId}
  and is_deleted = 0;
