mapper.select:
select exists(select null
              from comment
              where comment = #{comment}
                and user_id = #{username}
                and bookmark_id = #{bookmarkId}
                and is_deleted = 0)

mapper.update:
update comment
set is_deleted = 1
where id = #{id}

update comment
set is_deleted = 1
where user_id = #{userId}

update comment
set comment = #{comment,jdbcType=VARCHAR}
where id = #{id}
