mapper.select:
select tag, web_id
from tag
where tag = #{tagName,jdbcType=VARCHAR}
  and web_id = #{webId,jdbcType=INTEGER}
  and is_deleted = 0
limit 1;

select web_id
from tag
where tag = #{tagName,jdbcType=VARCHAR}
  and is_deleted = 0
limit #{from}, #{size};

mapper.update:
update tag
set is_deleted = 1
where tag = #{tagName,jdbcType=VARCHAR}
  and web_id = #{webId,jdbcType=INTEGER};

update tag
set is_deleted = 1
where web_id = #{webId,jdbcType=INTEGER};

mapper.select:
select count(distinct tag)
from tag
where is_deleted = 0;

select count(tag)
from tag
where tag = #{tagName}
  and is_deleted = 0;
