mapper.select:
select count(web_id) bookmarkNumber, u.user_name userName from website w left join user u on w.user_name = u.user_name
u.user_name = #{userName}
w.is_public = 1 and is_deleted = 0
group by userName order by bookmarkNumber desc;

mapper.update:
update user
set is_deleted = 1
where user_id = #{userId};

mapper.select:
select user_id, user_name, `password`, creation_time, `role`
from user
where user_name = #{userName}
  and password = #{password}
  and is_deleted = 0
limit 1;

select role
from user
where user_id = #{userId}
  and is_deleted = 0
limit 1;

select role
from user
where user_name = #{userName}
  and is_deleted = 0
limit 1;

select user_id, user_name, `password`, creation_time, `role`
from user
where user_name = #{userName}
  and is_deleted = 0
limit 1;

select count(*)
from user
where is_deleted = 0;

select user_id
from user
where user_name = #{userName}
  and password = #{password}
  and is_deleted = 0
limit 1;

select user_id
from user
where user_name = #{username}
  and is_deleted = 0
limit 1;
