mapper.select:
select count(b.id) bookmarkNumber, u.user_name userName from bookmark b inner join user u on b.user_name = u.user_name
u.user_name = #{userName}
b.is_public = 1 and u.is_deleted = 0 and b.is_deleted = 0
group by userName order by bookmarkNumber desc;

mapper.update:
update user
set is_deleted = 1
where id = #{id};

mapper.select:
select id, user_name, `password`, creation_time, `role`
from user
where user_name = #{userName}
  and password = #{password}
  and is_deleted = 0
limit 1;

select role
from user
where id = #{id}
  and is_deleted = 0
limit 1;

select role
from user
where user_name = #{userName}
  and is_deleted = 0
limit 1;

select id, user_name, `password`, creation_time, `role`
from user
where user_name = #{userName}
  and is_deleted = 0
limit 1;

select count(*)
from user
where is_deleted = 0;

select id
from user
where user_name = #{userName}
  and password = #{password}
  and is_deleted = 0
limit 1;

select id
from user
where user_name = #{username}
  and is_deleted = 0
limit 1;
